mapper.update:
    update SmsBatch
    set 
       10
              total=#{total,jdbcType=INTEGER} 
       10
              succ=#{succ,jdbcType=INTEGER} 
       10
              fail=#{fail,jdbcType=INTEGER} 
       10
              status=#{status,jdbcType=INTEGER} 
       10
              phone=#{phone,jdbcType=VARCHAR} 
       10
              content=#{content,jdbcType=VARCHAR} 
       10
              sendTime=#{sendTime,jdbcType=TIMESTAMP} 
       10
              createTime=#{createTime,jdbcType=TIMESTAMP} 
       10
              excuteTime=#{excuteTime,jdbcType=TIMESTAMP} 
where id = #{id,jdbcType=INTEGER}

mapper.select:
select 

from SmsBatch where 1=1
      
       and id = #{id}
      
      
       and total = #{total}
      
      
       and succ = #{succ}
      
      
       and fail = #{fail}
      
      
       and status = #{status}
      
      
       and phone = #{phone}
      
      
       and content = #{content}
      
      
       and sendTime = #{sendTime}
      
      
       and createTime = #{createTime}
      
      
       and excuteTime = #{excuteTime}

